import { useState, useEffect } from 'react';
import { FaThermometerEmpty } from 'react-icons/fa';

function App() {
  const [weathers, setWeathers] = useState([]);
  const [city, setCity] = useState('');
  const [lasCity, setLastCity] = useState('')
  const [loading, setLoading] = useState(false)

  function submitHandler(event) {
    event.preventDefault()
    setLastCity(city)
  }

  useEffect(() => {
    async function getCity() {
      const response = await fetch(`https://goweather.herokuapp.com/weather/${city}`)
      const data = await response.json();
      setWeathers(data)
    }

    getCity()
  }, [lasCity])

  return (
    <div>
      <form action='submit' onSubmit={submitHandler}>
        <input type='text' placeholder='Ex: São Paulo' value={city} onChange={event => setCity(event.target.value)} />
        <button type='submit'>Search City</button>
      </form>

      {lasCity && weathers && (
        <div>
          <h1>{lasCity}</h1>
          <h2>Current Weather</h2>
          <h4><strong>{weathers.temperature}</strong></h4>
          <h4><strong>{weathers.description}</strong></h4>
          <h4><strong>{weathers.wind}</strong></h4>
        </div>
      )}
    </div>
  );
}


export default App;
