import { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [weathers, setWeathers] = useState([]);
  const [city, setCity] = useState('');
  const [lasCity, setLastCity] = useState('')

  async function getCity() {
    try {
      const response = await axios.get(`https://goweather.herokuapp.com/weather/${city}`)
      setCity(response.data)
    } catch (error) {
      console.error(error);
    }
  }

  function submitHandler(event) {
    event.preventDefault()
    getCity()
  }

  useEffect(() => {
    async function getCity() {
      try {
        const response = await axios.get(`https://goweather.herokuapp.com/weather/${city}`)
        setCity(response.data)
      } catch (error) {
        console.error(error);
      }

      getCity()
    }
  }, [])

  return (
    <div>
      <form action='submit' onSubmit={submitHandler}>
        <input type='text' placeholder='Ex: São Paulo' value={city} onChange={event => setCity(event.target.value)} />
        <button type='submit'>Pesquisar Cidade</button>
      </form>

      {city && (
        <div>
          <h1>{city}</h1>
        </div>
      )}
    </div>

    // <div className='App'>
    //   <input type={'text'} onChange={changedCityHandler}/>

    //   <h1>{city}</h1>
    //   <p>{weathers.temperature}</p>
    //   <p>{weathers.description}</p>
    //   <p>{weathers.wind}</p>
    //   {console.log(weathers)}
    // </div>
  );
}


export default App;
